mapper.select:

select

  distinct


from log_pattern_set




  group by ${groupByClause}


  order by ${orderByClause}


  limit ${limitValue}


select

  distinct


from log_pattern_set




  group by ${groupByClause}


  order by ${orderByClause}

limit 0,1

mapper.update:

update log_pattern_set
set is_delete = 1 

 where 1=1 and id = #{id,jdbcType=BIGINT}

mapper.insert:

insert into log_pattern_set (id, app_id, log_type, log_export_type, log_pattern, create_time, create_user, update_time, update_user, create_user_name, update_user_name ) values 

  (#{item.id,jdbcType=BIGINT}, #{item.appId,jdbcType=VARCHAR}, #{item.logType,jdbcType=VARCHAR}, 
    #{item.logExportType,jdbcType=VARCHAR}, #{item.logPattern,jdbcType=VARCHAR}, #{item.createTime,jdbcType=TIMESTAMP}, 
    #{item.createUser,jdbcType=BIGINT}, #{item.updateTime,jdbcType=TIMESTAMP}, #{item.updateUser,jdbcType=BIGINT}, 
    #{item.createUserName,jdbcType=VARCHAR}, #{item.updateUserName,jdbcType=VARCHAR}
    )
